package Practice;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 20;
		int n2 = 10;
		
		int sum = n1 + n2;
		int substraction = n1 - n2;
		int divison = n1 / n2;
		int multiplication = n1 * n2;
		int remainder = n1 % n2;
		
		System.out.println(sum+","+substraction+","+divison+","+
		multiplication+","+remainder);
		
		

	}

}
