package Practice;

public class IncrementandDecrement1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int apples = 0;
		System.out.println(apples);
		System.out.println(++apples);
		System.out.println(apples);
		System.out.println(apples++);
		System.out.println(apples);
		apples--;
		System.out.println(apples);
		System.out.println(--apples);
		
		int pears = 3;
		int basket = ++pears * 5/ pears -- + --pears;
			

	}

}
